/* eslint-disable no-unused-expressions */
import React, { useState, useEffect } from 'react';
import { Row, Col, Button, Divider, Card } from 'antd';
import { useHistory, useParams } from 'react-router-dom';
import Magnifier from 'react-magnifier';
import '../../App.css';
import axios from '../../axios';
import { CheckCircleFilled } from '@ant-design/icons';

const ViewProducts = () => {
	const { Meta } = Card;
	const [productData, setProductData] = useState();
	const [previewImage, setPreviewImage] = useState('');
	const [key, setKey] = useState('Description');
	let history = useHistory();
	const { productId } = useParams();
	console.log(`productData`, productData);
	async function getProductData() {
		const req = await axios.get(`/product/${productId}`);
		if (req) {
			console.log(`data of particular product`, req?.data);
			setPreviewImage(req?.data[0]?.ImageURLOne?.url);
			setProductData(req?.data[0]);
		}
	}
	useEffect(() => {
		getProductData();
	}, [productId]);

	const tabList = [
		{
			key: 'Description',
			tab: 'Description',
		},
		{
			key: 'Price',
			tab: 'Price',
		},
		{
			key: 'Category',
			tab: 'Category',
		},
		{
			key: 'Rating',
			tab: 'Rating',
		},
	];

	const contentList = {
		Description: productData && productData?.product_description,
		Price: productData && productData?.product_price,
		Category: productData && productData?.product_category,
		Rating: productData && productData?.product_rating,
	};

	const onTabChange = (key) => {
		console.log(key);
		setKey(key);
	};
	return (
		<>
			<div>
				<Row gutter={[24, 12]} className="px-12">
					<Col xl={24} lg={24} md={24} sm={24} xs={24}>
						{productData && (
							<>
								<div className=" mt-12 shadow rounded-lg p-4 flex justify-between">
									<div className="font-bold text-lg">{productData?.product_name}</div>
									<div>
										<Button
											type="primary"
											onClick={() => {
												console.log(`productData`, productData);
											}}
										>
											<CheckCircleFilled /> Approve
										</Button>
									</div>
								</div>

								<div className="my-6 shadow rounded-lg">
									{productData && (
										<>
											<Row gutter={[24, 12]} className="border">
												<Col xl={8} lg={8} md={8} sm={24} xs={24}>
													<div style={{ maxHeight: '470px', overflow: 'auto' }}>
														<Card
															className="m-3 "
															onClick={() => {
																setPreviewImage(productData?.ImageURLOne?.secure_url);
															}}
															hoverable
															style={{ width: 240 }}
															cover={
																<img
																	alt="example"
																	src={productData?.ImageURLOne?.secure_url}
																/>
															}
														>
															<Meta title="View" />
														</Card>
														<Card
															className="m-3 "
															onClick={() => {
																setPreviewImage(productData?.ImageURLTwo?.url);
															}}
															hoverable
															style={{ width: 240 }}
															cover={
																<img
																	alt="example"
																	src={productData?.ImageURLTwo?.url}
																/>
															}
														>
															<Meta title="View" />
														</Card>
														<Card
															className="m-3 "
															onClick={() => {
																setPreviewImage(productData?.ImageURLThree?.url);
															}}
															hoverable
															style={{ width: 240 }}
															cover={
																<img
																	alt="example"
																	src={productData?.ImageURLThree?.url}
																/>
															}
														>
															<Meta title="View" />
														</Card>
														<Card
															className="m-3 "
															onClick={() => {
																setPreviewImage(productData?.ImageURLFour?.url);
															}}
															hoverable
															style={{ width: 240 }}
															cover={
																<img
																	alt="example"
																	src={productData?.ImageURLFour?.url}
																/>
															}
														>
															<Meta title="View" />
														</Card>
														<Card
															className="m-3 "
															onClick={() => {
																setPreviewImage(productData?.ImageURLFive?.url);
															}}
															hoverable
															style={{ width: 240 }}
															cover={
																<img
																	alt="example"
																	src={productData?.ImageURLFive?.url}
																/>
															}
														>
															<Meta title="View" />
														</Card>
													</div>
												</Col>
												<Divider type="vertical" />
												<Col xl={12} lg={12} md={12} sm={24} xs={24}>
													<Magnifier src={previewImage} width={'38rem'} zoomFactor={1.5} />
												</Col>
											</Row>
										</>
									)}
								</div>
								<div>
									<Card
										style={{ width: '100%' }}
										title="Product Info"
										tabList={tabList}
										activeTabKey={key}
										onTabChange={(key) => {
											onTabChange(key, 'key');
										}}
									>
										{contentList?.key}
									</Card>
								</div>
							</>
						)}
					</Col>
				</Row>
			</div>
		</>
	);
};

export default ViewProducts;
