/* eslint-disable no-unused-expressions */
import React, { useState, useEffect } from 'react';
import { Row, Col, Button, Divider, Card } from 'antd';
import { useHistory, useParams } from 'react-router-dom';
import Magnifier from 'react-magnifier';
import '../../App.css';
import axios from '../../axios';
import { CheckCircleFilled } from '@ant-design/icons';

const ViewProducts = () => {
	const { Meta } = Card;
	const [productData, setProductData] = useState();
	const [previewImage, setPreviewImage] = useState('');
	let history = useHistory();
	const { productId } = useParams();
	console.log(`productData`, productData);
	async function getProductData() {
		const req = await axios.get(`/product/${productId}`);
		if (req) {
			console.log(`data of particular product`, req?.data);
			setPreviewImage(req?.data[0]?.ImageUrlOne?.url);
			setProductData(req?.data[0]);
		}
	}
	useEffect(() => {
		getProductData();
	}, [productId]);

	return (
		<>
			<div>
				<Row gutter={[24, 12]} className="px-12">
					<Col span={4} />
					<Col xl={18} lg={18} md={18} sm={24} xs={24}>
						{productData && (
							<>
								<div className=" mt-12 shadow rounded-lg p-4 flex justify-between">
									<div className="font-bold text-lg">{productData?.product_name}</div>
									<div>
										<Button
											type="primary"
											onClick={() => {
												console.log(`productData`, productData);
											}}
										>
											<CheckCircleFilled /> Approve
										</Button>
									</div>
								</div>

								<div className="mt-6 shadow rounded-lg">
									<Row gutter={[24, 12]}>
										<Col xl={12} lg={12} md={12} sm={24} xs={24}>
											<Card
												onClick={() => {
													setPreviewImage(productData?.ImageUrlOne?.secure_url);
												}}
												hoverable
												style={{ width: 240 }}
												cover={
													<img
														alt="example"
														src="https://res.cloudinary.com/dxixjouyh/image/upload/v1616305832/vvtfyaed3e7utyyyqcuy.png"
													/>
												}
											>
												<Meta title="View" />
											</Card>
											<Card
												onClick={() => {
													setPreviewImage(productData?.ImageUrlTwo?.url);
												}}
												hoverable
												style={{ width: 240 }}
												cover={<img alt="example" src={productData?.ImageUrlTwo?.url} />}
											>
												<Meta title="View" />
											</Card>
											<Card
												onClick={() => {
													setPreviewImage(productData?.ImageUrlThree?.url);
												}}
												hoverable
												style={{ width: 240 }}
												cover={<img alt="example" src={productData?.ImageUrlThree?.url} />}
											>
												<Meta title="View" />
											</Card>
											<Card
												onClick={() => {
													setPreviewImage(productData?.ImageUrlFour?.url);
												}}
												hoverable
												style={{ width: 240 }}
												cover={<img alt="example" src={productData?.ImageUrlFour?.url} />}
											>
												<Meta title="View" />
											</Card>
											<Card
												onClick={() => {
													setPreviewImage(productData?.ImageUrlFive?.url);
												}}
												hoverable
												style={{ width: 240 }}
												cover={<img alt="example" src={productData?.ImageUrlFive?.url} />}
											>
												<Meta title="View" />
											</Card>
										</Col>
										<Divider type="vertical" />
										<Col xl={12} lg={12} md={12} sm={24} xs={24}>
											<Magnifier src={previewImage} width={500} />
											{/* <iframe
												title="Documents Preview"
												src={perviewImage}
												className="h-full text-center w-full"
												frameBorder="0"
											/> */}
										</Col>
									</Row>
								</div>
							</>
						)}
					</Col>
					<Col span={2} />
				</Row>
			</div>
		</>
	);
};

export default ViewProducts;
