/* eslint-disable no-unused-expressions */
import React from 'react';
import { Row, Col, Form, Input, Button, notification, Card } from 'antd';
import { useHistory } from 'react-router-dom';
import '../../App.css';
import axios from '../../axios';

const AddProducts = () => {
	let history = useHistory();
	const [form] = Form.useForm();

	return (
		<>
			<div>
				<Row gutter={[24, 12]} className="px-12">
					<Col span={6} />
					<Col xl={12} lg={12} md={12} sm={24} xs={24}>
						<div className="mt-12 ">
							<Card
								title="Welcome to FlyBuy!!!"
								bordered={false}
								className="md:w-5/6 rounded-full shadow"
								extra={<a href="/log-in">Already a user? Log In </a>}
							>
								<Form
									hideRequiredMark
									name="basic"
									//keep track of what user write in input field
									autoComplete="off"
									form={form}
									onFinish={(values) => {
										console.log('values', values);

										// form?.resetFields();
									}}
								>
									<Form.Item
										label={<span className="">First Name</span>}
										name="first_name"
										rules={[
											{
												required: true,
												message: 'Please input your first name!',
											},
										]}
									>
										<Input placeholder="Enter your first name" />
									</Form.Item>
									<Form.Item
										label={<span className="">Last Name</span>}
										name="last_name"
										rules={[
											{
												required: true,
												message: 'Please input your last name!',
											},
										]}
									>
										<Input placeholder="Enter your last name" />
									</Form.Item>

									<Form.Item
										label={<span className="">Username</span>}
										name="username"
										rules={[
											{
												required: true,
												message: 'Please input your username!',
											},
										]}
									>
										<Input placeholder="Enter your username" />
									</Form.Item>

									<Form.Item
										label={<span className="">Password</span>}
										name="password"
										rules={[
											{
												required: true,
												message: 'Please input your password!',
											},
										]}
									>
										<Input.Password placeholder="Enter your passowrd" />
									</Form.Item>

									<Form.Item>
										<Button type="primary" onClick={() => form?.submit()}>
											Become a new member
										</Button>
									</Form.Item>
								</Form>
							</Card>
						</div>
					</Col>
					<Col span={6} />
				</Row>
			</div>
		</>
	);
};

export default AddProducts;
