/* eslint-disable no-unused-expressions */
import React, { useState, useEffect } from 'react';
import { Row, Col, Input, Table, Tag, Space } from 'antd';
import { useHistory } from 'react-router-dom';
import '../../App.css';
import axios from '../../axios';

const AllProducts = () => {
	const [productList, setProductList] = useState([]);
	console.log(`productList`, productList);
	async function getProducts() {
		console.log(`2`);
		const req = await axios.get('/product/all');
		if (req) {
			setProductList(req?.data);
		}
	}
	useEffect(() => {
		console.log(`1`);
		getProducts();
	}, []);
	let history = useHistory();
	const columns = [
		{
			title: 'Product Name',
			dataIndex: 'product_name',
			render: (text) => <div>{text}</div>,
		},
		{
			title: 'Age',
			dataIndex: 'age',
			key: 'age',
		},
		{
			title: 'Address',
			dataIndex: 'address',
			key: 'address',
		},
		{
			title: 'Tags',
			key: 'tags',
			dataIndex: 'tags',
			render: (tags) => (
				<>
					{tags.map((tag) => {
						let color = tag.length > 5 ? 'geekblue' : 'green';
						if (tag === 'loser') {
							color = 'volcano';
						}
						return (
							<Tag color={color} key={tag}>
								{tag.toUpperCase()}
							</Tag>
						);
					})}
				</>
			),
		},
		{
			title: 'Action',
			key: 'action',
			render: (text, record) => (
				<Space size="middle">
					<div>Invite {record.name}</div>
					<div>Delete</div>
				</Space>
			),
		},
	];

	return (
		<>
			<div>
				<Row gutter={[24, 12]} className="px-12">
					<Col span={4} />
					<Col xl={18} lg={18} md={18} sm={24} xs={24}>
						<Table scroll={{ x: 400, y: 300 }} columns={columns} dataSource={productList} />
					</Col>
					<Col span={2} />
				</Row>
			</div>
		</>
	);
};

export default AllProducts;
