import { Row, Col } from 'antd';
import axios from '../../axios';
import { useHistory } from 'react-router-dom';

const ViewRetailer = () => {
	const [retailerData, setRetailerData] = useState();
	async function getRetailer() {
		const req = await axios.get(`/retailer/${retailerId}`);
		if (req) {
			setRetailerData(req?.data);
		}
	}

	useEffect(() => {
		getRetailer();
	}, [retailerId]);
	return (
		<div>
			<Row gutter={[24, 12]} className="px-12">
				<Col xl={8} lg={8} md={8} sm={24} xs={24}>
					{retailerData && (
						<div className="p-2 bg-gray-100 rounded shadow">
							<div>Name:- jfirjf</div>
							<div>Plan:- jfirjf</div>
							<div>Price:- jfirjf</div>
						</div>
					)}
				</Col>
				<Col xl={16} lg={16} md={16} sm={24} xs={24}>
					fqfe
				</Col>
			</Row>
		</div>
	);
};

export default ViewRetailer;
