/* eslint-disable no-unused-expressions */
import React from 'react';
import { Row, Col, Form, Input, Button, notification, Card, InputNumber, Select } from 'antd';
import { useHistory } from 'react-router-dom';
import '../../App.css';
import axios from '../../axios';

const AddProducts = () => {
	let history = useHistory();
	const [form] = Form.useForm();
	const { Option } = Select;
	return (
		<>
			<div>
				<Row gutter={[24, 12]} className="px-12">
					<Col span={4} />
					<Col xl={18} lg={18} md={18} sm={24} xs={24}>
						<div className="mt-12 ">
							<Card title="Add Product!!!" bordered={false} className="md:w-5/6 rounded-full shadow">
								<Form
									hideRequiredMark
									name="basic"
									//keep track of what user write in input field
									autoComplete="off"
									form={form}
									onFinish={(values) => {
										console.log('values', values);

										// form?.resetFields();
									}}
								>
									<Form.Item
										label={<span className="">Product Name</span>}
										name="product_name"
										rules={[
											{
												required: true,
												message: 'Please input the product name!',
											},
										]}
									>
										<Input placeholder="Enter product name" size={'large'} />
									</Form.Item>
									<Row gutter={[24, 12]}>
										<Col xl={12} lg={12} md={12} sm={24} xs={24}>
											<Form.Item
												label={<span className="">Product Price</span>}
												name="product_price"
												rules={[
													{
														required: true,
														message: 'Please input the product price!',
													},
												]}
											>
												<InputNumber
													min="0"
													max="2000"
													step="0.01"
													stringMode
													style={{ width: '100%' }}
													size={'large'}
													formatter={(value) =>
														`$ ${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')
													}
													parser={(value) => value.replace(/\$\s?|(,*)/g, '')}
												/>
											</Form.Item>
										</Col>
										<Col xl={12} lg={12} md={12} sm={24} xs={24}>
											<Form.Item
												label={<span className="">Product Rating</span>}
												name="product_rating"
												rules={[
													{
														required: true,
														message: 'Please input the product rating!',
													},
												]}
											>
												<InputNumber
													min="0"
													max="5"
													step="0.1"
													stringMode
													style={{ width: '100%' }}
													size={'large'}
													parser={(value) => value.replace(/\$\s?|(,*)/g, '')}
												/>
											</Form.Item>
										</Col>
									</Row>
									<Form.Item
										label={<span className="">Product Category</span>}
										name="product_category"
										rules={[
											{
												required: true,
												message: 'Please input the product category!',
											},
										]}
									>
										<Select
											mode="multiple"
											style={{ width: '100%' }}
											placeholder="select one country"
											defaultValue={['Men']}
											optionLabelProp="label"
										>
											<Option value="Men" label="Men">
												<div className="demo-option-label-item">Men</div>
											</Option>
											<Option value="Women" label="Women">
												<div className="demo-option-label-item">Women</div>
											</Option>
											<Option value="Children" label="Children">
												<div className="demo-option-label-item">Children</div>
											</Option>
											<Option value="Home_Accessories" label="Home Accessories">
												<div className="demo-option-label-item">Home Accessories</div>
											</Option>
										</Select>
									</Form.Item>
									<Form.Item
										label={<span className="">Username</span>}
										name="username"
										rules={[
											{
												required: true,
												message: 'Please input your username!',
											},
										]}
									>
										<Input placeholder="Enter your username" />
									</Form.Item>

									<Form.Item
										label={<span className="">Password</span>}
										name="password"
										rules={[
											{
												required: true,
												message: 'Please input your password!',
											},
										]}
									>
										<Input.Password placeholder="Enter your passowrd" />
									</Form.Item>

									<Form.Item>
										<Button type="primary" onClick={() => form?.submit()}>
											Become a new member
										</Button>
									</Form.Item>
								</Form>
							</Card>
						</div>
					</Col>
					<Col span={2} />
				</Row>
			</div>
		</>
	);
};

export default AddProducts;
