/* eslint-disable no-unused-expressions */
import React, { useState, useEffect } from 'react';
import { Row, Col, Button, Divider, Card, Tabs, Tag, notification } from 'antd';
import { useParams, useHistory } from 'react-router-dom';
import Magnifier from 'react-magnifier';
import '../../App.css';
import axios from '../../axios';
import { CheckCircleFilled, CloseCircleOutlined } from '@ant-design/icons';

const ViewProducts = () => {
	const { Meta } = Card;
	let history = useHistory();
	const [orderData, setOrderData] = useState();

	const { orderId } = useParams();
	console.log(`orderData`, orderData);
	async function getOrderData() {
		const req = await axios.get(`/order/${orderId}`);
		if (req) {
			console.log(`data of particular order`, req?.data);

			setOrderData(req?.data[0]);
		}
	}
	// async function approveProduct() {
	// 	const req = await axios.put(`/product/${productId}/approve`);
	// 	if (req) {
	// 		console.log(`data of approved product`, req);
	// 		if (req?.data?.ok === 1) {
	// 			notification.open({
	// 				message: 'Great Job!',
	// 				description: `${productData?.product_name} has been approved successfuly`,
	// 			});
	// 			history?.replace(`/products/all`);
	// 		}
	// 	}
	// }
	useEffect(() => {
		getOrderData();
	}, [orderId]);

	return (
		<>
			<div>
				<Row gutter={[24, 12]} className="px-12">
					<Col xl={24} lg={24} md={24} sm={24} xs={24}>
						{orderData && (
							<>
								<div
									className=" mt-12 shadow rounded-lg p-4 flex justify-between"
									style={{
										boxShadow: '10px 14px 18px #00ff66',
									}}
								>
									<div className="font-bold text-lg">#{orderData?._id}</div>
									<div className="flex">
										<div>
											<Button
												disabled={orderData?.status === 'APPROVED'}
												type="primary"
												onClick={() => {
													// approveProduct();
												}}
											>
												<CheckCircleFilled /> Approve
											</Button>
										</div>
										<div className="mx-3">
											<Button
												disabled={orderData?.status === 'REJECTED'}
												type="danger"
												onClick={() => {
													// rejectProduct();
												}}
											>
												<CloseCircleOutlined /> Reject
											</Button>
										</div>
									</div>
								</div>

								<div className="my-4 p-4 flex bg-gray-100 justify-between">
									{orderData?.admin_staus_comment && (
										<div className="flex ">
											<div className="bg-blue-800 rounded-full text-gray-100 py-1 px-4 mx-3">
												<i>i</i>
											</div>
											<div className="mx-2">{orderData?.admin_staus_comment}</div>
										</div>
									)}
									<div>
										<div className="bg-green-500 rounded-full text-gray-900 py-1 px-4 mx-3">
											<i>INR</i> {orderData?.amount}
										</div>
									</div>
								</div>

								<div className="my-6 shadow rounded-lg">
									<Row gutter={[24, 12]}>
										{orderData?.productsList?.map((item) => (
											<Col xl={8} lg={8} md={8} sm={24} xs={24}>
												<Card
													className="m-3 p-12"
													style={{ width: 300 }}
													hoverable
													cover={
														<img
															alt="example"
															src={item?.product?.ImageURLOne?.secure_url}
														/>
													}
												>
													<Meta title={item?.product?.product_name} />
												</Card>
											</Col>
										))}
									</Row>
								</div>
							</>
						)}
					</Col>
				</Row>
			</div>
		</>
	);
};

export default ViewProducts;
