/* eslint-disable no-unused-expressions */
import React, { useState, useEffect } from 'react';
import { Row, Col, Button, Divider, Card, Tabs, Tag, notification } from 'antd';
import { useParams, useHistory } from 'react-router-dom';
import Magnifier from 'react-magnifier';
import '../../App.css';
import axios from '../../axios';
import { CheckCircleFilled } from '@ant-design/icons';

const ViewProducts = () => {
	const { Meta } = Card;
	let history = useHistory();
	const [orderData, setOrderData] = useState();

	const { orderId } = useParams();
	console.log(`orderData`, orderData);
	async function getOrderData() {
		const req = await axios.get(`/order/${orderId}`);
		if (req) {
			console.log(`data of particular order`, req?.data);

			setOrderData(req?.data[0]);
		}
	}
	// async function approveProduct() {
	// 	const req = await axios.put(`/product/${productId}/approve`);
	// 	if (req) {
	// 		console.log(`data of approved product`, req);
	// 		if (req?.data?.ok === 1) {
	// 			notification.open({
	// 				message: 'Great Job!',
	// 				description: `${productData?.product_name} has been approved successfuly`,
	// 			});
	// 			history?.replace(`/products/all`);
	// 		}
	// 	}
	// }
	useEffect(() => {
		getOrderData();
	}, [orderId]);

	return (
		<>
			<div>
				<Row gutter={[24, 12]} className="px-12">
					<Col xl={24} lg={24} md={24} sm={24} xs={24}>
						{orderData && (
							<>
								<div
									className=" mt-12 shadow rounded-lg p-4 flex justify-between"
									style={{
										boxShadow: '10px 14px 18px #00ff66',
									}}
								>
									<div className="font-bold text-lg">#{orderData?._id}</div>
									<div className="flex">
										<div>
											<Button
												disabled={orderData?.status === 'APPROVED'}
												type="primary"
												onClick={() => {
													// approveProduct();
												}}
											>
												<CheckCircleFilled /> Approve
											</Button>
										</div>
										<div className="mx-3">
											<Button
												disabled={orderData?.status === 'REJECTED'}
												type="primary"
												onClick={() => {
													// rejectProduct();
												}}
											>
												<CheckCircleFilled /> Reject
											</Button>
										</div>
									</div>
								</div>
								<div className="my-4 p-4 flex bg-gray-100">
									<div className="bg-blue-800 text-gray-100 py-1 px-2">
										<i>i</i>
									</div>
								</div>
								<div className="my-6 shadow rounded-lg">
									{orderData && (
										<>
											<Row gutter={[24, 12]}>
												<Col xl={8} lg={8} md={8} sm={24} xs={24}>
													<div
														style={{
															marginTop: '10%',
															maxHeight: '500px',
															overflow: 'auto',
															paddingLeft: '10%',
														}}
														className="cardStyling"
													>
														{orderData?.productList &&
															orderData?.productList?.map(() => {
																<Card
																	className="m-3 p-12"
																	style={{ width: 300 }}
																	hoverable
																	cover={
																		<img
																			alt="example"
																			src={productData?.ImageURLOne?.secure_url}
																		/>
																	}
																>
																	<Meta title="View" />
																</Card>;
															})}
													</div>
												</Col>
											</Row>
										</>
									)}
								</div>
								<div className="my-4 shadow rounded-lg">
									<Tabs
										defaultActiveKey="Description"
										onChange={(key) => {
											console.log(key);
										}}
									>
										<TabPane tab={<span className="mx-3">Description</span>} key="Description">
											<div className="mx-4 " style={{ marginBottom: '10%' }}>
												{productData && productData?.product_description}
											</div>
										</TabPane>
										<TabPane tab={<span className="mx-3">Price</span>} key="Price">
											<div className="mx-4 " style={{ marginBottom: '10%' }}>
												INR {productData && productData?.product_price}
											</div>
										</TabPane>
										<TabPane tab={<span className="mx-3">Rating</span>} key="Rating">
											<div className="mx-4 " style={{ marginBottom: '10%' }}>
												{productData && productData?.product_rating}
											</div>
										</TabPane>
										<TabPane tab={<span className="mx-3">Category</span>} key="Category">
											<div className="mx-4 " style={{ marginBottom: '10%' }}>
												{productData &&
													productData?.product_category?.map((item) => {
														let color = item.length === 5 ? 'geekblue' : 'green';
														if (item === 'Children') {
															color = 'volcano';
														}
														if (item === 'Home Accessories') {
															color = 'Yellow';
														}
														return (
															<Tag color={color} key={item}>
																{item.toUpperCase()}
															</Tag>
														);
													})}
											</div>
										</TabPane>
									</Tabs>
								</div>
							</>
						)}
					</Col>
				</Row>
			</div>
		</>
	);
};

export default ViewProducts;
